import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import jsSha from 'jssha';


class Payments extends Component {
constructor(props) {
  super(props);
  this.state ={

    email: '',
    address: '',

    type: '',
    amount: 0,
    src2: '',
    dest: '',
    ref: '',

  }
  this.onChangeType = this.onChangeType.bind(this)
  this.componentDidMount=this.componentDidMount.bind(this)
}

onChangeType(event) {
  this.setState ({
    type: event.target.value
  })
}


componentDidMount() {
         const user = this.props.username //'ticklishswan772';
         const password =  this.props.password //'coltrane';
         const hmac = new jsSha('SHA-256', 'TEXT');
         hmac.setHMACKey(password, 'TEXT');
         hmac.update(user);
         hmac.update(Date.now().toString(36).substring(0, 4));
         const token = `${hmac.getHMAC('HEX')}%${user}`;
         const api = axios.create({
           baseURL: 'http://45.77.58.134:8080',
           headers: { 'Authorization': 'Bearer ' + token }
         });
         (async () => {
           const res = await api.get('/clients');
           const accounts = await api.get(`/accounts/${res.data[0]._id}`)
           const transactions = await api.get(`/transactions/${accounts.data[0]._id}/.*`)

           await api.post("/transactions", {
             "type": this.state.type,
             "src": accounts.data[0]._id,
             "dest": this.state.dest,
             "amount":this.state.amount,
             "ref":this.state.ref,
           })

           // await api.post("/transactions", {
           //   "type": this.state.type,
           //   "src": this.state.src2,
           //   "dest": this.state.dest,
           //   "amount":this.state.amount,
           //   "ref":this.state.ref,
           // })

          console.log(accounts);
         })();
       };




  render () {
    return (
      <div>

        <div className="navbar2">
          <NavLink to="/payments" className="navButton"> Once-off Payments </NavLink>
          <NavLink to="/scheduled" className="navButton"> Scheduled Payments </NavLink>

          <div className = "dropdown">
            <button className = "dropbtn"> Pay Beneficiary <i className = "fa fa-caret-down"></i></button>
          <div className = "dropdown-content">
          <NavLink to="/beneficiary" className="navButton"> Add New Beneficiary </NavLink>
          <NavLink to="/existing" className="navButton"> Existing Beneficiaries </NavLink>
        </div>
        </div>
          <NavLink to="/history" className="navButton"> Payment History </NavLink>
        </div>

      <div className="content">
        <div id="Once-off Payments" className="w3-container city">
        <form action="/action_page.php" >
          <h1>Once-off Payments</h1>
            <div id="payments">
              <h2> 1. Who would you like to pay? </h2>
              <p> Enter Name: <input className="imput" onChange={e=>{this.setState({dest: e.target.value.trim()})}} value={this.state.dest} /></p>
              <p> Account Type:
                <select className="imput" value={this.state.type} onChange={this.onChangeType}>
                  <option value="">Select:</option>
                  <option value="Savings">Savings</option>
                  <option value="Cheque">Cheque</option>
                  <option value="Debit">Debit</option>
                  <option value="Investment">Special Investment</option>
                  <option value="Credit">Credit</option>

                </select></p>
              <p> Account Number: <input className="imput" onChange={e=>{this.setState({src2: e.target.value.trim()})}} value={this.state.src2} /></p>

              <h2> 2. Payment Details </h2>
               <p>Enter amount to pay: <input className="imput"  onChange={e=>{this.setState({amount: e.target.value.trim()})}} value={this.state.amount} /></p>
               <p> Reference: <input className="imput"  onChange={e=>{this.setState({ref: e.target.value.trim()})}} value={this.state.ref} /></p>

              <h2> 3. Proof of Payment </h2>
              <p> Email Address: <input className="imput"  onChange={e=>{this.setState({email: e.target.value.trim()})}} value={this.state.email} /></p>
              <p> Addressed To: <input className="imput"  onChange={e=>{this.setState({address: e.target.value.trim()})}} value={this.state.address} /></p>
            </div>

          <div className="text" >
            <p><b>Please note</b></p>
            <p>Your payment may take up to 24 hours to reflect on Bank of the Sun accounts, and up to 2 business days with other banks. </p>
          </div>
          <input className='button' onClick={this.componentDidMount} value="Pay"/>
                <input className='button' onClick={this.componentDidMount} value="Cancel"/>
                  </form>
            </div>
          </div>
      </div>
    )
  }
}

export default Payments
