import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios';
import Login from './login'


 class Transfers extends Component {
   constructor(props) {
     super(props);
     this.state={

       amount: 0 ,
       ref: '',
       dest: '',
       src: '',
       type:'',
       accounts: []

     }
     this.selectFrom=this.selectFrom.bind(this)
     this.selectTo=this.selectTo.bind(this)
     this.componentDidMount=this.componentDidMount.bind(this)

   }



//const log = <Login user={this.state.username} pass={this.state.password} >

   componentDidMount() {

          const user = this.props.username //'ticklishswan772'
          const password = this.props.password //'coltrane'
          const hmac = new jsSHA('SHA-256', 'TEXT');
          hmac.setHMACKey(password, 'TEXT');
          hmac.update(user);
          hmac.update(Date.now().toString(36).substring(0, 4));
          const token = `${hmac.getHMAC('HEX')}%${user}`;
          const api = axios.create({
            baseURL: 'http://45.77.58.134:8080',
            headers: { 'Authorization': 'Bearer ' + token }

          });
          (async () => {
            const res = await api.get('/clients');
            const acc = await api.get(`/accounts/${res.data[0]._id}`)
            let trans = await api.get(`/transactions/${acc.data[0]._id}/.*`)


            await api.post("/transactions", {
              "type": this.state.type,
              "src":  this.state.dest,//acc.data[0]._id,
              "dest": this.state.src,//acc.data[0]._id,
              "amount": this.state.amount,
              "ref": this.state.ref,
              });

                // await api.post("/transactions", {
                //   "type": "cheque",
                //   "src": "e14f087f-8352-3fb5-9e9b-5804ec777c4f",
                //   "dest": acc.data[0]._id,
                //   "amount": -(this.state.amount),
                //   "ref": "tester",
                //   });


            trans = await api.get(`/transactions/${acc.data[0]._id}/.*`)

            console.log(trans);
          })();
        }


selectFrom (event) {
  this.setState ({
    type: event.target.value
  })
}

selectTo (event) {
  this.setState ({
    dest: event.target.value
  })
}


   render () {

     return (

       <div>
       <div className="navbar2">
         <NavLink to="/transfers" className="navButton"> Inter-Account Transfers </NavLink>
         </div>
         <div className="content">
         <div id="payments">
           <h1 style={this.h1Style}>Inter-Account Transfers</h1>

           <form action="/action_page.php" >
             <h2> 1. Accounts </h2>
             <p> Account Type:
               <select className="imput" value={this.state.src} onChange={this.selectFrom}>
               {this.state.accounts.map(x=>
                 <option key="{x._id}">{`${x.description}`}</option>
               )}
               </select>
                //  <option value="Debit">Debit</option>
                //  <option value="Investment">Investment</option>
                //  <option value="Cheque">Cheque</option>
                //  <option value="Savings">Savings</option>
                //  <option value="Credit">Credit</option>

             </p>

             <h2> 2. Amount </h2>
             <p> Enter amount to transfer: <input className="imput" onChange={e=>{this.setState({amount: e.target.value.trim()})}} value={this.state.amount} type="text" name="amount" /></p>




             <p> Destination Account: <input className="imput" onChange={e=>{this.setState({dest: e.target.value.trim()})}} value={this.state.dest} type="text" name="amount" /></p>


             <h2> 3. References </h2>
             <p> To Reference: <input className="imput" onChange={e=>{this.setState({ref:e.target.value.trim()})}} value={this.state.ref} type="text" name="amount" /></p>
             <p><b>Please note</b></p>
             <p>Your transfer may take up to 24 hours to reflect in your account.</p>


           </form>
           <button className='button' onClick={this.componentDidMount}>Transfer</button>

       </div>
     </div>

     </div>
     )
   }
 }


 export default Transfers
