import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios'

class Newacc extends Component {
constructor(props) {
  super(props);
  this.state={
    firstName: '',
    lastName: '',
    iD: '',
    salary: '',
    homeNum: '',
    workNum: '',
    cellNum: '',
    eMail: '',
    account: '',
    title: ''
  }
  this.onChangeAcc=this.onChangeAcc.bind(this)
  this.onChangeTitle=this.onChangeTitle.bind(this)
}

componentDidMount() {
          const user = 'lazymeercat116';
          const password = 'hottest';
          const hmac = new jsSHA('SHA-256', 'TEXT');
          hmac.setHMACKey(password, 'TEXT');
          hmac.update(user);
          hmac.update(Date.now().toString(36).substring(0, 4));
          const token = `${hmac.getHMAC('HEX')}%${user}`;
          const api = axios.create({
            baseURL: 'http://45.77.58.134:8080',
            headers: { 'Authorization': 'Bearer ' + token }
          });
          (async () => {
            const res = await api.get('/apply');
            const accounts = await api.get(`/accounts/${res.data[0]._id}`)
            this.setState({
              firstName: res.data,
              lastName: res.data,
              iD: res.data,
              salary: res.data,
              homeNum: res.data,
              workNum: res.data,
              cellNum: res.data,
              eMail: res.data,
              account: res.data,
              title: res.data

            });
            console.log(accounts);
          })();
        };



onChangeAcc(event) {
  this.setState({
    account: event.target.value
  })
}

onChangeTitle(event) {
  this.setState({
    title: event.target.value
  })
}

  render () {

    return (
      <div>
      <div className='navbar2'>
      <NavLink to="/newacc" className="navButton"> New Account </NavLink>
      </div>
          <h1>Create a New Account</h1>
          <div className="create">
            <h2> Accounts </h2>
            <p>
              <select className="imput" value={this.state.account} onChange={this.onChangeAcc}>
                <option value="">Select Account</option>
                <option value="Cheque">Cheque</option>
                <option value="Savings">Savings</option>
                <option value="Credit">Credit</option>
                <option value="Special Investment">Special Investment</option>
              </select>
              {this.state.account}
              </p>

            <h2> Fill in Account Details </h2>
            <div className="register">
            <h2>Personal Details</h2>
              <p>
                Title:
                <select className="imput" value={this.state.title} onChange={this.onChangeTitle}>
                  <option value="">Select Title</option>
                  <option value="Mr.">Mr.</option>
                  <option value="Dr.">Dr.</option>
                  <option value="Prof.">Prof.</option>
                  <option value="Sir">Sir</option>
                  <option value="Mrs.">Mrs</option>
                  <option value="Miss.">Miss</option>
                  <option value="other">other</option>
                </select>
                {this.state.title}
              </p>
              <p> First Name: <input className="imput" onChange={e=>{this.setState({firstName: e.target.value.trim()})}} value={this.state.firstName} type="text" name="amount" /></p>
              <p> Last Name: <input className="imput" onChange={e=>{this.setState({lastName: e.target.value.trim()})}} value={this.state.lastName} type="text" name="amount" /></p>
              <p> ID Number: <input className="imput" onChange={e=>{this.setState({iD: e.target.value.trim()})}} value={this.state.iD} type="text" name="amount" /></p>
              <h4 className="title">Contact Details</h4>
              <p> Salary: <input className="imput" onChange={e=>{this.setState({salary: e.target.value.trim()})}} value={this.state.salary} type="text" name="amount" /></p>
              <p>Home Phone Number: <input className="imput" onChange={e=>{this.setState({homeNum: e.target.value.trim()})}} value={this.state.homeNum} type="text" name="amount" /></p>
              <p> Work Phone Number: <input className="imput" onChange={e=>{this.setState({workNum: e.target.value.trim()})}} value={this.state.workNum} type="text" name="amount" /></p>
              <p> Cell Number: <input className="imput" onChange={e=>{this.setState({cellNum: e.target.value.trim()})}} value={this.state.cellNum} type="text" name="amount" /></p>
              <p> E-mail Address: <input className="imput" onChange={e=>{this.setState({eMail: e.target.value.trim()})}} value={this.state.eMail} type="text" name="amount" /></p>
            </div>
          </div>

          <div class="text">

            <button class="button" onClick={e=>console.log(this.state)}>Submit Application</button>
          </div>
      </div>
    )
  }
}

export default Newacc
