import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios';

class Electricity extends Component {
  constructor(props) {
    super(props);
    this.state={
      amount: 0,
    }
    this.selectFrom = this.selectFrom.bind(this)
    this.componentDidMount = this.componentDidMount.bind(this)
  }

  componentDidMount() {



         const user ='ticklishswan772'
         const password ='coltrane'
         const hmac = new jsSHA('SHA-256', 'TEXT');
         hmac.setHMACKey(password, 'TEXT');
         hmac.update(user);
         hmac.update(Date.now().toString(36).substring(0, 4));
         const token = `${hmac.getHMAC('HEX')}%${user}`;
         const api = axios.create({
           baseURL: 'http://45.77.58.134:8080',
           headers: { 'Authorization': 'Bearer ' + token }

         });
         (async () => {
           const res = await api.get('/clients');
           const acc = await api.get(`/accounts/${res.data[0]._id}`)
           let trans = await api.get(`/transactions/${acc.data[0]._id}/.*`)


           await api.post("/transactions", {
             "type": 'Electricity',
             "src": acc.data[0]._id,
             "dest": acc.data[0]._id,
             "amount": -(this.state.amount),
             "ref": 'Electricity'
             });
           trans = await api.get(`/transactions/${acc.data[0]._id}/.*`)

           console.log(trans);
         })();
       }

       selectFrom (event) {
         this.setState ({
           amount: event.target.value
         })
       }




  render () {
    return (
      <div>
      <div className="navbar2">
        <NavLink to="/utilities" className="navButton"> Airtime and Bundles </NavLink>
        <NavLink to="/buyElectricity" className="navButton"> Electricity </NavLink>
        </div>

      <div className="content">
        <div id="payments">
          <div id="Electricity" className="w3-container city" >
            <h1>Electricity</h1>

            <form action="/action_page.php" >
              <h2> 1. Details </h2>
              <p> Choose Recharge Amount:
                <select className="imput" value={this.state.amount} onChange={this.selectFrom}>
                  <option value="">Select:</option>
                  <option value="30">R30.00</option>
                  <option value="50">R50.00</option>
                  <option value="100">R100.00</option>
                  <option value="150">R150.00</option>
                  <option value="300">R300.00</option>
                  <option value="500">R500.00</option>
                  <option value="1000">R1000.00</option>
                </select></p>


            </form>
            <button className='button' onClick={this.componentDidMount}>Buy</button>

          </div>
        </div>
      </div>
      <button className="button" id="logout" onClick="logOut()">Log Out</button>
      </div>


    )
  }
}

export default Electricity
