import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import jsSHA from 'jssha';

class Beneficiary extends Component {
  constructor(props) {
    super(props);
    this.state ={
    n: '',
    nModified: '',
    ok: '',
    }
    this.onChangeAcc = this.onChangeAcc.bind(this)
    this.onChangeBank = this.onChangeBank.bind(this)
  }

  componentDidMount() {




         const user =  'ticklishswan772' //this.props.username
         const password =  'coltrane' //this.props.password
         const hmac = new jsSHA('SHA-256', 'TEXT');
         hmac.setHMACKey(password, 'TEXT');
         hmac.update(user);
         hmac.update(Date.now().toString(36).substring(0, 4));
         const token = `${hmac.getHMAC('HEX')}%${user}`;
         const api = axios.create({
           baseURL: 'http://45.77.58.134:8080',
           headers: { 'Authorization': 'Bearer ' + token }

         });
         (async () => {
           const res = await api.get('/clients');
           const acc = await api.get(`/accounts/${res.data[0]._id}`)



           await api.post("/generic", {
             "n": this.state.type,
             "nModified": acc.data[0]._id,
             "ok": acc.data[0]._id,

             });


           console.log(trans);
         })();
       }



  onChangeAcc(event) {
    this.setState ({
      acc: event.target.value
    })
  }

  onChangeBank(event) {
    this.setState ({
      bank: event.target.value
    })
  }

  onChangeAccType(event) {
    this.setState ({
      accType: event.target.value
    })
  }

  render () {
    return (
      <div>
        <div className="navbar2">
        <NavLink to="/payments" className="navButton"> Once-off Payments </NavLink>
        <NavLink to="/scheduled" className="navButton"> Scheduled Payments </NavLink>
        <NavLink to="/beneficiary" className="navButton"> Pay Beneficiaries </NavLink>
        <NavLink to="/history" className="navButton"> Payment History </NavLink>
      </div>

          <div className="content">
            <div id="Pay Beneficiaries" className="w3-container city">
          <h1>Beneficiaries</h1>



    <div id = "red">
    <div>
      <h2> 1. Choose Default Payment Account </h2>
        <p> From Account:
          <select className="imput" value={this.state.acc} onChange={this.onChangeAcc}>
      <option value="">Select:</option>
      <option value="Savings Account">Cheque</option>
      <option value="Student Account">Savings</option>
      <option value="Savings Account">Credit</option>
      <option value="Business Account">Special Investment</option>
          </select></p>
    <h2> 2. Beneficiary Details </h2>
      <p> Beneficiary Name: <input className="imput" onChange={e=>{this.setState({name: e.target.value.trim()})}} value={this.state.name} /></p>
      <p> Select Bank:
        <select className="imput" value={this.state.bank} onChange={this.onChangeBank}>
          <option value="">Select:</option>
          <option value="Savings Account">Absa</option>
          <option value="Student Account">Bank of the Sun</option>
          <option value="Savings Account">Capitec</option>
          <option value="Business Account">FNB</option>
          <option value="Savings Account">Nedbank</option>
          <option value="Investment Account">Standard Bank</option>
        </select></p>
        <p> Account Number: <input className="imput" onChange={e=>{this.setState({accNum: e.target.value.trim()})}} value={this.state.accNum} /></p>
        <p> Account Type:
          <select className="imput" value={this.state.accType} onChange={this.onChangeAccType}>
      <option value="">Select:</option>
      <option value="Savings Account">Cheque</option>
      <option value="Student Account">Savings</option>
      <option value="Savings Account">Credit</option>
      <option value="Business Account">Special Investment</option>
          </select></p>
       <p> Beneficiary Reference: <input className="imput" onChange={e=>{this.setState({benRef: e.target.value.trim()})}} value={this.state.benRef} /></p>
       <p> Own Reference: <input className="imput" onChange={e=>{this.setState({ownRef: e.target.value.trim()})}} value={this.state.ownRef} /></p>
      <h2> 3. Proof of Payment </h2>
      <p> Email Address: <input className="imput" onChange={e=>{this.setState({email: e.target.value.trim()})}} value={this.state.email} /></p>
      <p> Addressed To: <input className="imput" onChange={e=>{this.setState({address: e.target.value.trim()})}} value={this.state.address} /></p>
      <div>
      <p><b>Please note</b></p>
      <p>Your payment may take up to 24 hours to reflect on Bank of the Sun accounts, and up to 2 business days with other banks. </p>
      <input className='button' onClick={e=>console.log(this.state)} value="Add"/>
            <input className='button' onClick={e=>console.log(this.state)} value="Cancel"/>
      </div>


                  </div>
                  </div>
              </div>

              <button className="button" id="logout" onClick="logOut()">Log Out</button>
              </div>
        </div>
        )
      }
  }

export default Beneficiary
