import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';





class AccInfo extends Component {
  constructor(props) {
    super(props);
    this.state={
      cheque: '',
      savings: '',
      credit: '',
      inv: '',
    }
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick () {
  const  c = this.setState(state => ({
          cheque: alert('cheque acc'),
      }))
  const  s =  this.setState(state => ({
          savings: alert('savings acc'),
      }))
  const  cr = this.setState(state => ({
          credit: alert('credit acc'),
      }))
  const  i = this.setState(state => ({
          inv: alert('inv acc'),
      }))

  }


  render () {
    return (
      <div>
      <div className='navbar2'>
      <NavLink to="/accInfo" className="navButton"> Account Information </NavLink>
      </div>

      <div className="content">
        <div id="payments">
          <h1>Accounts</h1>
          <p> Select Account:
            <select className="imput">
              <option value="">Select Account</option>
              <option value="Student Account">Student Account</option>
              <option value="Business Account">Business Account</option>
              <option value="Investment Account">Investment Account</option>
              <option value="Savings Account">Savings Account</option>
            </select>
          </p>


        <center>
          <h1>Account Information</h1>
          <div className="btns">
            <button id="typebtn" className ="button"type="button" onClick={this.handleClick.c}>Cheque</button>
            <button id="typebtn" className ="button"type="button" onClick={this.handleClick.s}>Savings</button>
            <button id="typebtn" className ="button"type="button" onClick={this.handleClick.cr}>Credit</button>
            <button id="typebtn" className ="button"type="button" onClick={this.handleClick.i}>Special Investment</button>
          </div>
        </center>
        </div>
      </div>
      </div>
    )
  }
}

export default AccInfo
