import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios';
import Login from './login'


 class Transfers extends Component {
   constructor(props) {
     super(props);
     this.state={
       src: '',
       amount: 0 ,
       ref: '',
       dest: '',
       type:'',
       clients: [],
       accounts: [],
       transactions: [],
       account: [],

     }
     this.selectFrom=this.selectFrom.bind(this)
     this.selectTo=this.selectTo.bind(this)
     this.componentDidMount=this.componentDidMount.bind(this)
     this.onChange=this.onChange.bind(this)
     this.makePayments=this.makePayments.bind(this)

   }



//const log = <Login user={this.state.username} pass={this.state.password} >

   componentDidMount() {



          const user = 'blackfish787' //this.props.username
          const password = 'lestat' //this.props.password
          const hmac = new jsSHA('SHA-256', 'TEXT');
          hmac.setHMACKey(password, 'TEXT');
          hmac.update(user);
          hmac.update(Date.now().toString(36).substring(0, 4));
          const token = `${hmac.getHMAC('HEX')}%${user}`;
          const api = axios.create({
            baseURL: 'http://45.77.58.134:8080',
            headers: { 'Authorization': 'Bearer ' + token }

          });
          (async () => {
            const res = await api.get('/clients');
            this.setState({clients: res.data});
            const acc = await api.get(`/accounts/` + this.state.clients[0]["_id"])
            this.setState({accounts: acc.data})
          })()
        }

    makePayments(){
      const user = 'blackfish787' //this.props.username
      const password = 'lestat' //this.props.password
      const hmac = new jsSHA('SHA-256', 'TEXT');
      hmac.setHMACKey(password, 'TEXT');
      hmac.update(user);
      hmac.update(Date.now().toString(36).substring(0, 4));
      const token = `${hmac.getHMAC('HEX')}%${user}`;
      const api = axios.create({
        baseURL: 'http://45.77.58.134:8080',
        headers: { 'Authorization': 'Bearer ' + token }

      });

      api.post("/transactions", {
        time: this.state.time,
        type: "debit",
        src: this.state.src,
        dest: this.state.dest,
        amount: this.state.amount,
        ref: this.state.ref,
      }).then(function (response){
        console.log(response);
      }).catch(function (error){
        console.log(error)
      })

        api.post("/transactions", {
          time: this.state.time,
          type: "credit",
          src: this.state.dest,
          dest: this.state.src,
          amount: -(this.state.amount),
          ref: this.state.ref,
          }).then(function (response){
            console.log(response);
          }).catch(function (error){
            console.log(error)
          })
        }
onChange(e){
  this.setState({[e.target.name]: e.target.value})
  console.log(this.state.src)
}

selectFrom (event) {
  this.setState ({
    type: event.target.value
  })
}

selectTo (event) {
  this.setState ({
    dest: event.target.value
  })
}


   render () {

     return (

       <div>
       <div className="navbar2">
         <NavLink to="/transfers" className="navButton"> Inter-Account Transfers </NavLink>
         </div>
         <div className="content">
         <div id="payments">
           <h1 style={this.h1Style}>Inter-Account Transfers</h1>

           <form action="/action_page.php" >
             <h2> 1. Accounts </h2>
             <p> Account Type:
               <select className="imput" onChange={this.onChange}>
               {this.state.accounts.map(x=>
                 <option key={x._id}>{`${x.description}`}</option>
               )}
               </select>
             </p>
             <p>Destination Account:
             <select className="imput" onChange={this.onChange}>
             {this.state.accounts.map(x=>
               <option key={x._id}>{`${x.description}`}</option>
             )}
             </select>
           </p>


             <h2> 2. Amount </h2>
             <p> Enter amount to transfer: <input className="imput" onChange={e=>{this.setState({amount: e.target.value.trim()})}} value={this.state.amount} type="text" name="amount" /></p>






             <h2> 3. References </h2>
             <p> To Reference: <input className="imput" onChange={e=>{this.setState({ref:e.target.value.trim()})}} value={this.state.ref} type="text" name="amount" /></p>
             <p><b>Please note</b></p>
             <p>Your transfer may take up to 24 hours to reflect in your account.</p>


           </form>
           <button className='button' onClick={this.makePayments}>Transfer</button>

       </div>
     </div>

     </div>
     )
   }
 }


 export default Transfers
