import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import jsSHA from 'jssha';

class Beneficiary extends Component {
  constructor(props) {
    super(props);
    this.state ={
      acc: '',
      name: '',
      bank: '',
      accNum: '',
      accType: '',
      benRef: '',

      email: '',
      address: '',
      payAmt1: '',
      payAmt2: '',
      payAmt3: '',
      myRef1: '',
      myRef2: '',
      myRef3: '',
      theirRef1: '',
      theirRef2: '',
      theirRef3: '',

      ok: '', //type
      nModified: '', //ref
      n: '', //src

    }
    this.onChangeAccType = this.onChangeAccType.bind(this)

  }

  componentDidMount() {




         const user =  'ticklishswan772' //this.props.username
         const password =  'coltrane' //this.props.password
         const hmac = new jsSHA('SHA-256', 'TEXT');
         hmac.setHMACKey(password, 'TEXT');
         hmac.update(user);
         hmac.update(Date.now().toString(36).substring(0, 4));
         const token = `${hmac.getHMAC('HEX')}%${user}`;
         const api = axios.create({
           baseURL: 'http://45.77.58.134:8081',
           headers: { 'Authorization': 'Bearer ' + token }

         });
         (async () => {
           const res = await api.get('/clients');
           const acc = await api.get(`/accounts/${res.data[0]._id}`)
           const ben = await api.get('/generic/upserted[0]._id')
           const newBen = await api.post("/generic", {
             "n": this.state.n, // src
             "nModified": this.state.nModified, //ref
             "ok": this.state.ok, // type
             "_id":ben.upserted[0]._id
             });

           console.log(ben);
         })();
       }

  onChangeAccType(event) {
    this.setState ({
      ok: event.target.value
    })
  }

  render () {
    return (
      <div>
        <div className="navbar2">
        <NavLink to="/payments" className="navButton"> Once-off Payments </NavLink>
        <NavLink to="/scheduled" className="navButton"> Scheduled Payments </NavLink>
        <NavLink to="/beneficiary" className="navButton"> Pay Beneficiaries </NavLink>
        <NavLink to="/history" className="navButton"> Payment History </NavLink>
      </div>

          <div className="content">
            <div id="Pay Beneficiaries" className="w3-container city">
          <h1>Beneficiaries</h1>



    <div id = "red">
    <div>

    <h2> 2. Beneficiary Details </h2>

        <p> Account Number: <input className="imput" onChange={e=>{this.setState({n: e.target.value.trim()})}} value={this.state.n} /></p>
        <p> Account Type:
          <select className="imput" value={this.state.ok} onChange={this.onChangeAccType}>
      <option value="">Select:</option>
      <option value="Cheque">Cheque</option>
      <option value="Savings">Savings</option>
      <option value="Credit">Credit</option>
      <option value="Special Investment">Special Investment</option>
          </select></p>
       <p> Beneficiary Reference: <input className="imput" onChange={e=>{this.setState({nModified: e.target.value.trim()})}} value={this.state.nModified} /></p>

      <button className='button' onClick={this.componentDidMount}> Add </button>
            <button className='button' onClick={e=>console.log(this.state)} >Cancel</button>
      </div>
        </div>
          <button className="button" id="logout" onClick="logOut()">Log Out</button>
          </div>
            </div>
            </div>
        )
      }
  }

export default Beneficiary
