import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios';
import Login from './login'

class Transfers extends Component {
  constructor(props) {
    super(props);
    this.state={

      amount: 0 ,
      ref: '',
      dest: '',
      src: '',
      type:'',
      time: '',
      accounts:[],

    }
    this.selectFrom=this.selectFrom.bind(this)
    this.selectTo=this.selectTo.bind(this)
    this.componentDidMount=this.componentDidMount.bind(this)
    this.onChange=this.onChange.bind(this)

  }



//const log = <Login user={this.state.username} pass={this.state.password} >

  componentDidMount() {



         const user = 'blacktiger144'//this.props.username //'ticklishswan772'
         const password = '191919' //this.props.password //'coltrane'
         const hmac = new jsSHA('SHA-256', 'TEXT');
         hmac.setHMACKey(password, 'TEXT');
         hmac.update(user);
         hmac.update(Date.now().toString(36).substring(0, 4));
         const token = `${hmac.getHMAC('HEX')}%${user}`;
         const api = axios.create({
           baseURL: 'http://45.77.58.134:8080',
           headers: { 'Authorization': 'Bearer ' + token }

         });
         (async () => {
           const res = await api.get('/clients');
           const acc = await api.get(`/accounts/${res.data[0]._id}`)
           this.setState({accounts: acc.data})
           let trans = await api.get(`/transactions/${acc.data[0]._id}/.*`)


           await api.post("/transactions", {
             time: '',
             type: "debit",
             src: this.state.src,
             dest: this.state.dest,
             amount: this.state.amount,
             ref: this.state.ref,
             });

            await api.post("/transactions", {
              time: '',
              type: "credit",
              src: this.state.dest,
              dest: this.state.src,
              amount: -(this.state.amount),
              ref: this.state.ref,
              })


           trans = await api.get(`/transactions/${acc.data[0]._id}/.*`)

           console.log(trans);
         })();
       }
 onChange(e){
  this.setState({[e.target.name]: e.target.value})
  console.log(this.state.src)
}

selectFrom (event) {
 this.setState ({
   src: event.target.value
 })
}

selectTo (event) {
 this.setState ({
   dest: event.target.value
 })
}


  render () {

    return (

      <div>
      <div className="navbar2">
        <NavLink to="/transfers" className="navButton"> Inter-Account Transfers </NavLink>
        </div>
        <div className="content">
        <div id="payments">
          <h1 style={this.h1Style}>Inter-Account Transfers</h1>


            <h2> 1. Accounts </h2>
            <p> Account From:  </p>
              <select className="imput" name="src"  onChange={this.onChange}>
              {this.state.accounts.map(x =>
                <option key={x._id}>{`${x.description}`}</option>
              )}

              </select>

              <p> Account To:  </p>
                <select className="imput" name="dest"  onChange={this.onChange}>
                {this.state.accounts.map(x =>
                  <option key={x._id}>{`${x.description}`}</option>
                )}

                </select>


            <h2> 2. Amount </h2>
            <p> Enter amount to transfer: <input className="imput" onChange={e=>{this.setState({amount: e.target.value.trim()})}} value={this.state.amount} type="text" name="amount" /></p>






            <h2> 3. References </h2>
            <p> To Reference: <input className="imput" onChange={e=>{this.setState({ref:e.target.value.trim()})}} value={this.state.ref} type="text" name="ref" /></p>
            <p><b>Please note</b></p>
            <p>Your transfer may take up to 24 hours to reflect in your account.</p>



          <button className='button' onClick={this.componentDidMount}>Transfer</button>

      </div>
    </div>

    </div>
    )
  }
}
 export default Transfers
