import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios'


 class Transfers extends Component {
   constructor(props) {
     super(props);
     this.state={
       amount: '',
       fromRef: '',
       toRef: '',
       accountFrom: '',
       accountTo: '',
     }
     this.selectFrom=this.selectFrom.bind(this)
     this.selectTo=this.selectTo.bind(this)
     this.componentDidMount=this.componentDidMount.bind(this)
     this.amountTrans=this.amountTrans.bind(this)
   }

amountTrans() {

}

   componentDidMount() {
     let x = {
       "amount": 30,
       "dest": "febec4ad-3472-41ac-b43a-d31591ea978f",
       "ref": "402878",
       "src": "4d57aa2f-a0fb-39d7-b6ca-e584a7bc5324",
       "time": 1435533455204,
       "type": "credit"
     }

          const user = 'lazymeercat116';
          const password = 'hottest';
          const hmac = new jsSHA('SHA-256', 'TEXT');
          hmac.setHMACKey(password, 'TEXT');
          hmac.update(user);
          hmac.update(Date.now().toString(36).substring(0, 4));
          const token = `${hmac.getHMAC('HEX')}%${user}`;
          const api = axios.create({
            baseURL: 'http://45.77.58.134:8080',
            headers: { 'Authorization': 'Bearer ' + token },
            data: x
          });
          (async () => {
            const res = await api.get('/clients');
            const Balance = await api.get(`/accounts/${res.data[0]._id}`);
            const newbal = Balance.data[0].balance - this.state.amount;
            const transAmount = await api.post(`/transactions`)
            const checkBal = await api.get(`/transactions/${Balance.data[0]._id}/.*`)
            //this.setState({amount: res.data});
            console.log(checkBal);
          })();
        }


selectFrom (event) {
  this.setState ({
    accountFrom: event.target.value
  })
}

selectTo (event) {
  this.setState ({
    accountTo: event.target.value
  })
}


   render () {
     return (
       <div>
       <div className="navbar2">
         <NavLink to="/transfers" className="navButton"> Inter-Account Transfers </NavLink>
         </div>
         <div className="content">
         <div id="payments">
           <h1 style={this.h1Style}>Inter-Account Transfers</h1>

           <form action="/action_page.php" >
             <h2> 1. Accounts </h2>
             <p> Transfer From:
               <select className="imput" value={this.state.accountFrom} onChange={this.selectFrom}>
                 <option value="">Select Account</option>
                 <option value="Student Account">Student Account</option>
                 <option value="Business Account">Business Account</option>
                 <option value="Investment Account">Investment Account</option>
                 <option value="Savings Account">Savings Account</option>
               </select>
             </p>
             <p> Transfer To:
               <select className="imput" value={this.state.accountTo} onChange={this.selectTo}>
                 <option value="">Select Account</option>
                 <option value="Student Account">Student Account</option>
                 <option value="Business Account">Business Account</option>
                 <option value="Investment Account">Investment Account</option>
                 <option value="Savings Account">Savings Account</option>
               </select>
             </p>
             <h2> 2. Amount </h2>
             <p> Enter amount to transfer: <input className="imput" onChange={e=>{this.setState({amount: e.target.value.trim()})}} value={this.state.amount} type="text" name="amount" /></p>
             <h2> 3. References </h2>
             <p> From Reference: <input className="imput" onChange={e=>{this.setState({fromRef: e.target.value.trim()})}} value={this.state.fromRef} type="text" name="amount" /></p>
             <p> To Reference: <input className="imput" onChange={e=>{this.setState({toRef:e.target.value.trim()})}} value={this.state.toRef} type="text" name="amount" /></p>
             <p><b>Please note</b></p>
             <p>Your transfer may take up to 24 hours to reflect in your account.</p>


           </form>
           <button className='button' onClick={this.componentDidMount}>Transfer</button>
       </div>
     </div>
     <button className="button" id="logout" >Log Out</button>
     </div>
     )
   }
 }

 export default Transfers
