import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import jsSHA from 'jssha';


/**
*  Fetches API
*  Initalise the state
*  Populates the table
**/

class Statements extends Component {
  constructor() {
    super()
    this.state = { account: {}, transactions: [] };
  }

  componentDidMount() {
        const user = 'blackfish787';
        const password = 'lestat';
        const hmac = new jsSHA('SHA-256', 'TEXT');
        hmac.setHMACKey(password, 'TEXT');
        hmac.update(user);
        hmac.update(Date.now().toString(36).substring(0, 4));
        const token = `${hmac.getHMAC('HEX')}%${user}`;
        const api = axios.create({
        baseURL: 'http://45.77.58.134:8080',
        headers: { 'Authorization': 'Bearer ' + token }
        });

 (async () => {
   const id = this.props.match.params.accid;
   console.log(id);
           const res = await api.get('/clients');
           const accounts = await api.get(`/accounts/${res.data[0]._id}`)
           const transactions = await api.get(`/transactions/${id}/.*`)
           for (let x = 0; x < accounts.data.length; x++) {
             if (accounts.data[x]._id === id) {
               this.setState({account: accounts.data[x]});
               break;
             }
           }
           this.setState({transactions: transactions.data});
           // console.log(accounts);
         })();
       };


  render () {
         return (
         <div>
         <div className = "navbar2">
           <NavLink to="/MyAccounts" className = "navButton"> My Accounts </NavLink>
           <NavLink to="/newAcc" className = "navButton"> New Accounts </NavLink>
           <NavLink to="/myCards" className = "navButton"> My Cards </NavLink>
           <NavLink to="/accInfo" className = "navButton"> Account Information </NavLink>
         </div>

         <div className = "content">
          <div id="payments">
            <h1>Statements</h1>
            <h2>{this.state.account.description}</h2>
            <table>
            <thead>
            <th>Date</th>
            <th>Reference</th>
            <th>Amount</th>
            <th>Balance</th>
            <th></th>
            </thead>
            <tbody>
            {this.state.transactions.map(x =>
            <tr key = {x._id}>
            <td>{Intl.DateTimeFormat('en-GB', {
              year: 'numeric',
              month: 'long',
              day: '2-digit',
              hour: '2-digit',
              minute: '2-digit',
              second: '2-digit'
            }).format(x.time)}</td>
            <td>{x.ref}</td>
            <td>{Intl.NumberFormat('en-GB', {
              style: 'currency',
              currency: 'ZAR',
            }).format(x.amount)}</td>
            <td>{Intl.NumberFormat('en-GB', {
              style: 'currency',
              currency: 'ZAR',
            }).format(x.balance)}</td>
          </tr>
        )}
        </tbody>
        </table>
        <NavLink to ="/printStatements" className = "button"> Print </NavLink>
        <NavLink to ="/downloadStatements" className = "button"> Download </NavLink>
        </div>
        </div>
        </div>
      )
      }
    }

export default Statements
