import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios'

class Newacc extends Component {
constructor(props) {
  super(props);
  this.state={
    _id: '',
    balance: 0,
    client: '',
    description: '',
    type: '',



  }
  this.onChangeAcc=this.onChangeAcc.bind(this)
  this.onChangeTitle=this.onChangeTitle.bind(this)
  this.componentDidMount=this.componentDidMount.bind(this)
}

componentDidMount() {
          const user = 'lazymeercat116';
          const password = 'hottest';
          const hmac = new jsSHA('SHA-256', 'TEXT');
          hmac.setHMACKey(password, 'TEXT');
          hmac.update(user);
          hmac.update(Date.now().toString(36).substring(0, 4));
          const token = `${hmac.getHMAC('HEX')}%${user}`;
          const api = axios.create({
            baseURL: 'http://45.77.58.134:8080',
            headers: { 'Authorization': 'Bearer ' + token }
          });
          (async () => {
            const res = await api.get('/clients');
            const acc = await api.get(`/accounts/${res.data[0]._id}`)
            const newacc = await api.post('/apply', {
              "_id": acc.data[0]._id,
              "balance": acc.data[0].balance,
              "client": acc.data[0].client,
              "description": acc.data[0].description,
              "type": this.state.type,
            })

            console.log(acc)
          })();
        };


onChangeAcc(event) {
  this.setState({
    type: event.target.value
  })
}

onChangeTitle(event) {
  this.setState({
    title: event.target.value
  })
}

  render () {

    return (
      <div>
      <div className='navbar2'>
      <NavLink to="/newacc" className="navButton"> New Account </NavLink>
      </div>
          <h1>Create a New Account</h1>
          <div className="create">
            <h2> Accounts </h2>
            <p>
              <select className="imput" value={this.state.type} onChange={this.onChangeAcc}>
                <option value="">Select Account</option>
                <option value="Cheque">Cheque</option>
                <option value="Savings">Savings</option>
                <option value="Credit">Credit</option>
                <option value="Special Investment">Special Investment</option>
              </select>

              </p>

            <h2> Fill in Account Details </h2>
            <div className="register">

              <p> ID Number: <input className="imput" onChange={e=>{this.setState({_id: e.target.value.trim()})}} value={this.state._id} type="text" name="amount" /></p>

              <p> Account Description: <input className="imput" onChange={e=>{this.setState({description: e.target.value.trim()})}} value={this.state.description} type="text" name="amount" /></p>
            </div>
          </div>

          <div className="text">

            <button className="button" onClick={this.componentDidMount}>Submit Application</button>
          //e=>console.log(this.state)
          </div>
      </div>
    )
  }
}

export default Newacc
