import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import axios from 'axios';
import jsSha from 'jssha';

class Scheduled extends Component {
  constructor(props) {
    super(props);
    this.state ={
      fromAcc: '',
      payAmount: '',
      payName: '',
      name: '',
      bank: '',
      accNum: '',
      type: '',
      benRef: '',
      ownRef: '',
      email: '',
      address: '',
      intervals: '',
      fromDate: '',
      toDate: '',
    }
    this.onChangeBank = this.onChangeBank.bind(this)
    this.onChangeAccount = this.onChangeAccount.bind(this)
    this.onChangeType = this.onChangeType.bind(this)
    this.onChangeIntervals = this.onChangeIntervals.bind(this)
  }

  onChangeBank(event) {
    this.setState ({
      bank: event.target.value
    })
  }

  onChangeAccount(event) {
    this.setState ({
      fromAcc: event.target.value
    })
  }

  onChangeType(event) {
    this.setState ({
      type: event.target.value
    })
  }

  onChangeIntervals(event) {
    this.setState ({
      intervals: event.target.value
    })
  }


  componentDidMount() {
           const user = 'lazymeercat116';
           const password = 'hottest';
           const hmac = new jsSha('SHA-256', 'TEXT');
           hmac.setHMACKey(password, 'TEXT');
           hmac.update(user);
           hmac.update(Date.now().toString(36).substring(0, 4));
           const token = `${hmac.getHMAC('HEX')}%${user}`;
           const api = axios.create({
             baseURL: 'http://45.77.58.134:8080',
             headers: { 'Authorization': 'Bearer ' + token }
           });

   }

  render () {
    return (
      <div>

        <div className="navbar2">
          <NavLink to="/payments" className="navButton"> Once-off Payments </NavLink>
          <NavLink to="/scheduled" className="navButton"> Scheduled Payments </NavLink>

          <div className = "dropdown">
            <button className = "dropbtn"> Pay Beneficiary <i className = "fa fa-caret-down"></i></button>
          <div className = "dropdown-content">
          <NavLink to="/beneficiary" className="navButton"> Add New Beneficiary </NavLink>
          <NavLink to="/existing" className="navButton"> Existing Beneficiaries </NavLink>
        </div>
        </div>
          <NavLink to="/history" className="navButton"> Payment History </NavLink>
        </div>

        <div className="content">
          <div id="Scheduled Payments" className="w3-container city">
            <h1>Scheduled Payments</h1>

            <div id="payments">
            <h2> 1. Scheduled Payment Details </h2>
                <p> From Account:
                  <select className="imput" value={this.state.fromAcc} onChange={this.onChangeAccount}>
              <option value="">Select:</option>
              <option value="Savings Account">Cheque</option>
              <option value="Student Account">Savings</option>
              <option value="Savings Account">Credit</option>
              <option value="Business Account">Special Investment</option>
                  </select></p>
               Payment Amount: <input className="imput" onChange={e=>{this.setState({payAmount: e.target.value.trim()})}} value={this.state.payAmount} />
              <p> Scheduled Payment Name: <input className="imput" onChange={e=>{this.setState({payName: e.target.value.trim()})}} value={this.state.payName} /></p>
            <h2> 2. Beneficiary Details </h2>
              <p> Enter Name: <input className="imput" onChange={e=>{this.setState({name: e.target.value.trim()})}} value={this.state.name}/></p>
              <p> Select Bank:
                <select className="imput" value={this.state.bank} onChange={this.onChangeBank}>
                  <option value="">Select:</option>
                  <option value="Savings Account">Absa</option>
                  <option value="Student Account">Bank of the Sun</option>
                  <option value="Savings Account">Capitec</option>
                  <option value="Business Account">FNB</option>
                  <option value="Savings Account">Nedbank</option>
                  <option value="Investment Account">Standard Bank</option>
                </select></p>
              <p> Account Number: <input className="imput" onChange={e=>{this.setState({accNum: e.target.value.trim()})}} value={this.state.accNum} /></p>
              <p> Account Type:
                <select className="imput" value={this.state.type} onChange={this.onChangeType}>
                  <option value="">Select:</option>
                  <option value="Savings Account">Cheque</option>
                  <option value="Student Account">Savings</option>
                  <option value="Savings Account">Credit</option>
                  <option value="Business Account">Special Investment</option>
                </select></p>
              <p> Beneficiary Reference: <input className="imput" onChange={e=>{this.setState({benRef: e.target.value.trim()})}} value={this.state.benRef} /></p>
              <p> Own Reference: <input className="imput" onChange={e=>{this.setState({ownRef: e.target.value.trim()})}} value={this.state.ownRef} /></p>
            <h2> 3. Notification Method </h2>
              <p> Email Address: <input className="imput" onChange={e=>{this.setState({email: e.target.value.trim()})}} value={this.state.email} /></p>
              <p> Addressed To: <input className="imput" onChange={e=>{this.setState({address: e.target.value.trim()})}} value={this.state.address} /></p>
            <h2> 4. Scheduled Payment Dates </h2>
              <p> Payment Intervals:
                <select className="imput" value={this.state.type} onChange={this.onChangeIntervals}>
                  <option value="">Select:</option>
                  <option value="type">Daily</option>
                  <option value="type">Weekly</option>
