import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import jsSHA from 'jssha';
import axios from 'axios'


 class DownloadStatements extends Component {
   constructor(props) {
     super(props);
     this.state={
       fromDate: " ",
       toDate: " ",
     }
   }

     componentDidMount() {
           const user = 'beautifulmouse787';
           const password = 'yogibear';
           const hmac = new jsSHA('SHA-256', 'TEXT');
           hmac.setHMACKey(password, 'TEXT');
           hmac.update(user);
           hmac.update(Date.now().toString(36).substring(0, 4));
           const token = `${hmac.getHMAC('HEX')}%${user}`;
           const api = axios.create({
           baseURL: 'http://45.77.58.134:8080',
           headers: { 'Authorization': 'Bearer ' + token }
           });
         }


   render () {
     return (
       <div>
       <div className="navbar2">
         <NavLink to="/MyAccounts" className = "navButton"> My Accounts </NavLink>
         <NavLink to="/NewAcc" className = "navButton"> New Accounts </NavLink>
         <NavLink to="/MyCards" className = "navButton"> My Cards </NavLink>
         <NavLink to="/AccInfo" className = "navButton"> Account Information </NavLink>
       </div>
         <div className="content">
         <div id="payments">
           <h1 style={this.h1Style}>Download Statements</h1>
           <form action="/action_page.php" >
           <p>Search for Statements:</p>
             <p> From Date: <input className="imput" onChange={e=>{this.setState({fromDate: e.target.value.trim()}); console.log(new Date(e.target.value).getTime() / 1000);}} value={this.state.fromDate} type="date" name="amount" /></p>
             <p> To Date: <input className="imput" onChange={e=>{console.log(e.target.value); this.setState({toDate: e.target.value})}} value={this.state.toDate} type="date" name="amount" /></p>
           </form>
           <button className='button' onClick={this.componentDidMount}>Continue</button>
       </div>
     </div>
     <button className="button" id="logout" >Log Out</button>
     </div>
   );
   }

}
 export default DownloadStatements
